import{_ as o,e}from"./app.77cde27d.js";const c={},d=e('<h2 id="_1-\u524D\u8A00" tabindex="-1"><a class="header-anchor" href="#_1-\u524D\u8A00" aria-hidden="true">#</a> 1 \u524D\u8A00</h2><blockquote><p>\u9762\u8BD5\u4E2D\u7684\u5B89\u5168\u95EE\u9898\uFF0C\u660E\u786E\u6765\u8BF4\uFF0C\u5C31\u4E24\u4E2A\u65B9\u9762\uFF1A</p></blockquote><ul><li><code>CSRF</code>\uFF1A\u57FA\u672C\u6982\u5FF5\u3001\u653B\u51FB\u65B9\u5F0F\u3001\u9632\u5FA1\u63AA\u65BD</li><li><code>XSS</code>\uFF1A\u57FA\u672C\u6982\u5FF5\u3001\u653B\u51FB\u65B9\u5F0F\u3001\u9632\u5FA1\u63AA\u65BD</li></ul><blockquote><p>\u8FD9\u4E24\u4E2A\u95EE\u9898\uFF0C\u4E00\u822C\u4E0D\u4F1A\u95EE\u592A\u96BE\u3002</p></blockquote><blockquote><p>\u6709\u4EBA\u95EE\uFF1A<code>SQL</code>\u6CE8\u5165\u7B97\u5417\uFF1F\u7B54\u6848\uFF1A\u8FD9\u4E2A\u5176\u5B9E\u8DDF\u524D\u7AEF\u7684\u5173\u7CFB\u4E0D\u662F\u5F88\u5927\u3002</p></blockquote><h2 id="_2-csrf" tabindex="-1"><a class="header-anchor" href="#_2-csrf" aria-hidden="true">#</a> 2 CSRF</h2><blockquote><p>\u95EE\u7684\u4E0D\u96BE\uFF0C\u4E00\u822C\u95EE\uFF1A</p></blockquote><ul><li><code>CSRF</code>\u7684\u57FA\u672C\u6982\u5FF5\u3001\u7F29\u5199\u3001\u5168\u79F0</li><li>\u653B\u51FB\u539F\u7406</li><li>\u9632\u5FA1\u63AA\u65BD</li></ul><blockquote><p>\u5982\u679C\u628A<strong>\u653B\u51FB\u539F\u7406</strong>\u548C<strong>\u9632\u5FA1\u63AA\u65BD</strong>\u638C\u63E1\u597D\uFF0C\u57FA\u672C\u6CA1\u4EC0\u4E48\u95EE\u9898\u3002</p></blockquote><h3 id="_2-1-csrf\u7684\u57FA\u672C\u6982\u5FF5\u3001\u7F29\u5199\u3001\u5168\u79F0" tabindex="-1"><a class="header-anchor" href="#_2-1-csrf\u7684\u57FA\u672C\u6982\u5FF5\u3001\u7F29\u5199\u3001\u5168\u79F0" aria-hidden="true">#</a> 2.1 CSRF\u7684\u57FA\u672C\u6982\u5FF5\u3001\u7F29\u5199\u3001\u5168\u79F0</h3><blockquote><p><code>CSRF</code>\uFF08<code>Cross-site request forgery</code>\uFF09\uFF1A<strong>\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020</strong>\u3002</p></blockquote><p>PS\uFF1A\u4E2D\u6587\u540D\u4E00\u5B9A\u8981\u8BB0\u4F4F\u3002\u82F1\u6587\u5168\u79F0\uFF0C\u5982\u679C\u8BB0\u4E0D\u4F4F\u4E5F\u62C9\u5012\u3002</p><h3 id="_2-2-csrf\u7684\u653B\u51FB\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#_2-2-csrf\u7684\u653B\u51FB\u539F\u7406" aria-hidden="true">#</a> 2.2 CSRF\u7684\u653B\u51FB\u539F\u7406</h3><p><img src="https://gitee.com/jiangjingmin/picbed/raw/master/kyxiao/interview/20180307_1735.png" alt="image"></p><blockquote><p>\u7528\u6237\u662F\u7F51\u7AD9A\u7684\u6CE8\u518C\u7528\u6237\uFF0C\u4E14\u767B\u5F55\u8FDB\u53BB\uFF0C\u4E8E\u662F\u7F51\u7AD9A\u5C31\u7ED9\u7528\u6237\u4E0B\u53D1<code>cookie</code>\u3002</p></blockquote><blockquote><p>\u4ECE\u4E0A\u56FE\u53EF\u4EE5\u770B\u51FA\uFF0C\u8981\u5B8C\u6210\u4E00\u6B21<code>CSRF</code>\u653B\u51FB\uFF0C\u53D7\u5BB3\u8005\u5FC5\u987B\u6EE1\u8DB3\u4E24\u4E2A\u5FC5\u8981\u7684\u6761\u4EF6\uFF1A</p></blockquote><ol><li>\u767B\u5F55\u53D7\u4FE1\u4EFB\u7F51\u7AD9<code>A</code>\uFF0C\u5E76\u5728\u672C\u5730\u751F\u6210<code>Cookie</code>\u3002\uFF08\u5982\u679C\u7528\u6237\u6CA1\u6709\u767B\u5F55\u7F51\u7AD9<code>A</code>\uFF0C\u90A3\u4E48\u7F51\u7AD9<code>B</code>\u5728\u8BF1\u5BFC\u7684\u65F6\u5019\uFF0C\u8BF7\u6C42\u7F51\u7AD9<code>A</code>\u7684<code>api</code>\u63A5\u53E3\u65F6\uFF0C\u4F1A\u63D0\u793A\u4F60\u767B\u5F55\uFF09</li><li>\u5728\u4E0D\u767B\u51FA<code>A</code>\u7684\u60C5\u51B5\u4E0B\uFF0C\u8BBF\u95EE\u5371\u9669\u7F51\u7AD9<code>B</code>\uFF08\u5176\u5B9E\u662F\u5229\u7528\u4E86\u7F51\u7AD9<code>A</code>\u7684\u6F0F\u6D1E\uFF09\u3002</li></ol><blockquote><p>\u6211\u4EEC\u5728\u8BB2<code>CSRF</code>\u65F6\uFF0C\u4E00\u5B9A\u8981\u628A\u4E0A\u9762\u7684\u4E24\u70B9\u8BF4\u6E05\u695A\u3002</p></blockquote><blockquote><p>\u6E29\u99A8\u63D0\u793A\u4E00\u4E0B\uFF0C<code>cookie</code>\u4FDD\u8BC1\u4E86\u7528\u6237\u53EF\u4EE5\u5904\u4E8E\u767B\u5F55\u72B6\u6001\uFF0C\u4F46\u7F51\u7AD9<code>B</code>\u5176\u5B9E\u62FF\u4E0D\u5230 <code>cookie</code>\u3002</p></blockquote><blockquote><p>\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u524D\u6BB5\u65F6\u95F4\u91CC\uFF0C\u5FAE\u535A\u7F51\u7AD9\u6709\u4E2A<code>api</code>\u63A5\u53E3\u6709\u6F0F\u6D1E\uFF0C\u5BFC\u81F4\u5F88\u591A\u7528\u6237\u7684\u7C89\u4E1D\u66B4\u589E\u3002</p></blockquote><h3 id="_2-3-csrf\u5982\u4F55\u9632\u5FA1" tabindex="-1"><a class="header-anchor" href="#_2-3-csrf\u5982\u4F55\u9632\u5FA1" aria-hidden="true">#</a> 2.3 CSRF\u5982\u4F55\u9632\u5FA1</h3><p><strong>\u65B9\u6CD5\u4E00\u3001Token \u9A8C\u8BC1\uFF1A</strong>\uFF08\u7528\u7684\u6700\u591A\uFF09</p><ol><li>\u670D\u52A1\u5668\u53D1\u9001\u7ED9\u5BA2\u6237\u7AEF\u4E00\u4E2A<code>token</code>\uFF1B</li><li>\u5BA2\u6237\u7AEF\u63D0\u4EA4\u7684\u8868\u5355\u4E2D\u5E26\u7740\u8FD9\u4E2A<code>token</code>\u3002</li><li>\u5982\u679C\u8FD9\u4E2A <code>token</code> \u4E0D\u5408\u6CD5\uFF0C\u90A3\u4E48\u670D\u52A1\u5668\u62D2\u7EDD\u8FD9\u4E2A\u8BF7\u6C42\u3002</li></ol><p><strong>\u65B9\u6CD5\u4E8C\uFF1A\u9690\u85CF\u4EE4\u724C\uFF1A</strong></p><ul><li>\u628A <code>token</code> \u9690\u85CF\u5728 <code>http</code> \u7684 <code>head</code>\u5934\u4E2D\u3002</li></ul><blockquote><p>\u65B9\u6CD5\u4E8C\u548C\u65B9\u6CD5\u4E00\u6709\u70B9\u50CF\uFF0C\u672C\u8D28\u4E0A\u6CA1\u6709\u592A\u5927\u533A\u522B\uFF0C\u53EA\u662F\u4F7F\u7528\u65B9\u5F0F\u4E0A\u6709\u533A\u522B\u3002</p></blockquote><p><strong>\u65B9\u6CD5\u4E09\u3001Referer \u9A8C\u8BC1\uFF1A</strong></p><blockquote><p><code>Referer</code> \u6307\u7684\u662F\u9875\u9762\u8BF7\u6C42\u6765\u6E90\u3002\u610F\u601D\u662F\uFF0C<strong>\u53EA\u63A5\u53D7\u672C\u7AD9\u7684\u8BF7\u6C42\uFF0C\u670D\u52A1\u5668\u624D\u505A\u54CD\u5E94</strong>\uFF1B\u5982\u679C\u4E0D\u662F\uFF0C\u5C31\u62E6\u622A\u3002</p></blockquote><h2 id="_3-xss" tabindex="-1"><a class="header-anchor" href="#_3-xss" aria-hidden="true">#</a> 3 XSS</h2><h3 id="_3-1-xss\u7684\u57FA\u672C\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#_3-1-xss\u7684\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a> 3.1 XSS\u7684\u57FA\u672C\u6982\u5FF5</h3><blockquote><p>`XSS\uFF08Cross Site Scripting\uFF09``\uFF1A<strong>\u8DE8\u57DF\u811A\u672C\u653B\u51FB</strong>\u3002</p></blockquote><ul><li>\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u8BE6\u7EC6\u8BB2\u4E00\u4E0B <code>XSS</code> \u7684\u5185\u5BB9\u3002</li></ul><blockquote><p>\u9884\u5907\u77E5\u8BC6\uFF1A<code>HTTP</code>\u3001<code>Cookie</code>\u3001<code>Ajax</code>\u3002</p></blockquote><h3 id="_3-2-xss\u7684\u653B\u51FB\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#_3-2-xss\u7684\u653B\u51FB\u539F\u7406" aria-hidden="true">#</a> 3.2 XSS\u7684\u653B\u51FB\u539F\u7406</h3><blockquote><p><code>XSS</code>\u653B\u51FB\u7684\u6838\u5FC3\u539F\u7406\u662F\uFF1A\u4E0D\u9700\u8981\u4F60\u505A\u4EFB\u4F55\u7684\u767B\u5F55\u8BA4\u8BC1\uFF0C\u5B83\u4F1A\u901A\u8FC7\u5408\u6CD5\u7684\u64CD\u4F5C\uFF08\u6BD4\u5982\u5728<code>url</code>\u4E2D\u8F93\u5165\u3001\u5728\u8BC4\u8BBA\u6846\u4E2D\u8F93\u5165\uFF09\uFF0C\u5411\u4F60\u7684\u9875\u9762\u6CE8\u5165\u811A\u672C\uFF08\u53EF\u80FD\u662F<code>js</code>\u3001<code>hmtl</code>\u4EE3\u7801\u5757\u7B49\uFF09\u3002</p></blockquote><blockquote><p>\u6700\u540E\u5BFC\u81F4\u7684\u7ED3\u679C\u53EF\u80FD\u662F\uFF1A</p></blockquote><ul><li>\u76D7\u7528<code>Cookie</code></li><li>\u7834\u574F\u9875\u9762\u7684\u6B63\u5E38\u7ED3\u6784\uFF0C\u63D2\u5165\u5E7F\u544A\u7B49\u6076\u610F\u5185\u5BB9</li><li><code>D-doss</code>\u653B\u51FB</li></ul><h3 id="_3-3-xss\u7684\u653B\u51FB\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#_3-3-xss\u7684\u653B\u51FB\u65B9\u5F0F" aria-hidden="true">#</a> 3.3 XSS\u7684\u653B\u51FB\u65B9\u5F0F</h3><ol><li>\u53CD\u5C04\u578B</li></ol><blockquote><p>\u53D1\u51FA\u8BF7\u6C42\u65F6\uFF0C<code>XSS</code>\u4EE3\u7801\u51FA\u73B0\u5728<code>url</code>\u4E2D\uFF0C\u4F5C\u4E3A\u8F93\u5165\u63D0\u4EA4\u5230\u670D\u52A1\u5668\u7AEF\uFF0C\u670D\u52A1\u5668\u7AEF\u89E3\u6790\u540E\u54CD\u5E94\uFF0C<code>XSS</code>\u4EE3\u7801\u968F\u54CD\u5E94\u5185\u5BB9\u4E00\u8D77\u4F20\u56DE\u7ED9\u6D4F\u89C8\u5668\uFF0C\u6700\u540E\u6D4F\u89C8\u5668\u89E3\u6790\u6267\u884C<code>XSS</code>\u4EE3\u7801\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u50CF\u4E00\u6B21\u53CD\u5C04\uFF0C\u6240\u4EE5\u53EB\u53CD\u5C04\u578B<code>XSS</code>\u3002</p></blockquote><ol start="2"><li>\u5B58\u50A8\u578B</li></ol><blockquote><p>\u5B58\u50A8\u578B<code>XSS</code>\u548C\u53CD\u5C04\u578B<code>XSS</code>\u7684\u5DEE\u522B\u5728\u4E8E\uFF0C\u63D0\u4EA4\u7684\u4EE3\u7801\u4F1A\u5B58\u50A8\u5728\u670D\u52A1\u5668\u7AEF\uFF08\u6570\u636E\u5E93\u3001\u5185\u5B58\u3001\u6587\u4EF6\u7CFB\u7EDF\u7B49\uFF09\uFF0C\u4E0B\u6B21\u8BF7\u6C42\u65F6\u76EE\u6807\u9875\u9762\u65F6\u4E0D\u7528\u518D\u63D0\u4EA4XSS\u4EE3\u7801\u3002</p></blockquote><ol start="3"><li>\u57FA\u4E8EDOM\u578B</li></ol><h3 id="_3-4-xss\u7684\u9632\u8303\u63AA\u65BD-encode-\u8FC7\u6EE4" tabindex="-1"><a class="header-anchor" href="#_3-4-xss\u7684\u9632\u8303\u63AA\u65BD-encode-\u8FC7\u6EE4" aria-hidden="true">#</a> 3.4 XSS\u7684\u9632\u8303\u63AA\u65BD\uFF08encode + \u8FC7\u6EE4\uFF09</h3><p><strong>XSS\u7684\u9632\u8303\u63AA\u65BD\u4E3B\u8981\u6709\u4E09\u4E2A\uFF1A</strong></p><p><strong>1. \u7F16\u7801</strong>\uFF1A</p><blockquote><p>\u5BF9\u7528\u6237\u8F93\u5165\u7684\u6570\u636E\u8FDB\u884C<code>HTML Entity</code>\u7F16\u7801\u3002</p></blockquote><p>\u5982\u4E0A\u56FE\u6240\u793A\uFF0C\u628A\u5B57\u7B26\u8F6C\u6362\u6210 \u8F6C\u4E49\u5B57\u7B26\u3002</p><blockquote><p><code>Encode</code>\u7684\u4F5C\u7528\u662F\u5C06<code>$var</code>\u7B49\u4E00\u4E9B\u5B57\u7B26\u8FDB\u884C\u8F6C\u5316\uFF0C\u4F7F\u5F97\u6D4F\u89C8\u5668\u5728\u6700\u7EC8\u8F93\u51FA\u7ED3\u679C\u4E0A\u662F\u4E00\u6837\u7684\u3002</p></blockquote><p>\u6BD4\u5982\u8BF4\u8FD9\u6BB5\u4EE3\u7801\uFF1A</p><div class="language-html ext-html line-numbers-mode"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span><span class="token punctuation">&gt;</span></span><span class="token script"><span class="token language-javascript"><span class="token function">alert</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">)</span></span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">&gt;</span></span>\n</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br></div></div><blockquote><p>\u82E5\u4E0D\u8FDB\u884C\u4EFB\u4F55\u5904\u7406\uFF0C\u5219\u6D4F\u89C8\u5668\u4F1A\u6267\u884Calert\u7684js\u64CD\u4F5C\uFF0C\u5B9E\u73B0XSS\u6CE8\u5165\u3002</p></blockquote><blockquote><p>\u8FDB\u884C\u7F16\u7801\u5904\u7406\u4E4B\u540E\uFF0C\u5728\u6D4F\u89C8\u5668\u4E2D\u7684\u663E\u793A\u7ED3\u679C\u5C31\u662F<code>&lt;script&gt;alert(1)&lt;/script&gt;</code>\uFF0C\u5B9E\u73B0\u4E86\u5C06<code>$var</code>\u4F5C\u4E3A\u7EAF\u6587\u672C\u8FDB\u884C\u8F93\u51FA\uFF0C\u4E14\u4E0D\u5F15\u8D77<code>JavaScript</code>\u7684\u6267\u884C\u3002</p></blockquote><p><strong>2\u3001\u8FC7\u6EE4\uFF1A</strong></p><ul><li>\u79FB\u9664\u7528\u6237\u8F93\u5165\u7684\u548C\u4E8B\u4EF6\u76F8\u5173\u7684\u5C5E\u6027\u3002\u5982<code>onerror</code>\u53EF\u4EE5\u81EA\u52A8\u89E6\u53D1\u653B\u51FB\uFF0C\u8FD8\u6709<code>onclick</code>\u7B49\u3002\uFF08\u603B\u800C\u8A00\u662F\uFF0C\u8FC7\u6EE4\u6389\u4E00\u4E9B\u4E0D\u5B89\u5168\u7684\u5185\u5BB9\uFF09</li><li>\u79FB\u9664\u7528\u6237\u8F93\u5165\u7684<code>Style</code>\u8282\u70B9\u3001<code>Script</code>\u8282\u70B9\u3001<code>Iframe</code>\u8282\u70B9\u3002\uFF08\u5C24\u5176\u662F<code>Script</code>\u8282\u70B9\uFF0C\u5B83\u53EF\u662F\u652F\u6301\u8DE8\u57DF\u7684\u5440\uFF0C\u4E00\u5B9A\u8981\u79FB\u9664\uFF09\u3002</li></ul><p><strong>3\u3001\u6821\u6B63</strong></p><ul><li>\u907F\u514D\u76F4\u63A5\u5BF9<code>HTML Entity</code>\u8FDB\u884C\u89E3\u7801\u3002</li><li>\u4F7F\u7528<code>DOM Parse</code>\u8F6C\u6362\uFF0C\u6821\u6B63\u4E0D\u914D\u5BF9\u7684<code>DOM</code>\u6807\u7B7E\u3002</li></ul><blockquote><p>\u5907\u6CE8\uFF1A\u6211\u4EEC\u5E94\u8BE5\u53BB\u4E86\u89E3\u4E00\u4E0B<code>DOM Parse</code>\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u5B83\u7684\u4F5C\u7528\u662F\u628A\u6587\u672C\u89E3\u6790\u6210<code>DOM</code>\u7ED3\u6784\u3002</p></blockquote><p>\u6BD4\u8F83\u5E38\u7528\u7684\u505A\u6CD5\u662F\uFF0C\u901A\u8FC7\u7B2C\u4E00\u6B65\u7684\u7F16\u7801\u8F6C\u6210\u6587\u672C\uFF0C\u7136\u540E\u7B2C\u4E09\u6B65\u8F6C\u6210<code>DOM</code>\u5BF9\u8C61\uFF0C\u7136\u540E\u7ECF\u8FC7\u7B2C\u4E8C\u6B65\u7684\u8FC7\u6EE4\u3002</p><p><strong>\u8FD8\u6709\u4E00\u79CD\u7B80\u6D01\u7684\u7B54\u6848\uFF1A</strong></p><p>\u9996\u5148\u662Fencode\uFF0C\u5982\u679C\u662F\u5BCC\u6587\u672C\uFF0C\u5C31\u767D\u540D\u5355\u3002</p><h2 id="_4-csrf-\u548C-xss-\u7684\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#_4-csrf-\u548C-xss-\u7684\u533A\u522B" aria-hidden="true">#</a> 4 CSRF \u548C XSS \u7684\u533A\u522B</h2><blockquote><p>\u9762\u8BD5\u5B98\u8FD8\u53EF\u80FD\u559C\u6B22\u95EE\u4E8C\u8005\u7684\u533A\u522B\u3002</p></blockquote><p><strong>\u533A\u522B\u4E00\uFF1A</strong></p><ul><li><code>CSRF</code>\uFF1A\u9700\u8981\u7528\u6237\u5148\u767B\u5F55\u7F51\u7AD9<code>A</code>\uFF0C\u83B7\u53D6 <code>cookie</code></li><li><code>XSS</code>\uFF1A\u4E0D\u9700\u8981\u767B\u5F55\u3002</li></ul><p><strong>\u533A\u522B\u4E8C\uFF1A\uFF08\u539F\u7406\u7684\u533A\u522B\uFF09</strong></p><ul><li><code>CSRF</code>\uFF1A\u662F\u5229\u7528\u7F51\u7AD9<code>A</code>\u672C\u8EAB\u7684\u6F0F\u6D1E\uFF0C\u53BB\u8BF7\u6C42\u7F51\u7AD9<code>A</code>\u7684<code>api</code>\u3002</li><li><code>XSS</code>\uFF1A\u662F\u5411\u7F51\u7AD9 <code>A</code> \u6CE8\u5165 <code>JS</code>\u4EE3\u7801\uFF0C\u7136\u540E\u6267\u884C <code>JS</code> \u91CC\u7684\u4EE3\u7801\uFF0C\u7BE1\u6539\u7F51\u7AD9<code>A</code>\u7684\u5185\u5BB9\u3002</li></ul>',67);function t(a,s){return d}var n=o(c,[["render",t],["__file","\u5B89\u5168\u95EE\u9898\uFF1ACSRF\u548CXSS.html.vue"]]);export{n as default};
